Copy / Paste example, and call numberToAlphabet( number )<hr />function numberToAlphabet( string ){
	var alphaString = "ABCDEFGHIJKLMNOPQRSTUVWXYZ";
	var spots = ( string / 26 );

	if( spots > 1 ){
		if( ( string % 26 ) == 0 ){
			string = 26;
			spots=spots-1;
		}
		string = numberToAlphabet( spots.toString().split(''''.'''')[0] ) + numberToAlphabet( string % 26 );
	}else{
		string = alphaString.substr( string-1 , 1 );
	}
	
	return string;
}

